<script>
    kb_content.onscroll = function() {
        scrollFunc()
    };

    function scrollFunc() {
        if (kb_content.scrollTop > 20) {
            mybutton.style.display = "block";
        } else {
            mybutton.style.display = "none";
        }
    }
    
    // Get all list items
    const listItems = document.querySelectorAll('#numberedList li');

    // Add click event listener to each list item
    listItems.forEach(item => {
        item.addEventListener('click', function() {
            // Remove color from all list items
            listItems.forEach(item => {
                item.querySelector('a').classList.remove("clicked");
            });

            // Change color of clicked item
            this.querySelector('a').classList.add("clicked");
        });
    });
</script>